    constructor (uint256 ratex, address payable walletx, IERC20 tokenx)  {
        require(ratex > 0, "Crowdsale: rate is 0");
        require(walletx != address(0), "Crowdsale: wallet is the zero address");
        require(address(tokenx) != address(0), "Crowdsale: token is the zero address");

        _rate = ratex;
        _wallet = walletx;
        _token = tokenx;
