// import "./Crowdsale.sol";

import "./KycContract.sol";

import "./mint/MintedCrowdsale.sol";

contract MyTokenSale is MintedCrowdsale {


    KycContract kyc;

    constructor (
        uint256 rate,    // rate in TKNbits
        address payable wallet,
        IERC20 token,
        KycContract _kyc
    )
        MintedCrowdsale()
        Crowdsale(rate, wallet, token)
    {
